mapper.select:
select 单位ID as unitId, 单位名称 as unitName, 人员ID as personId, 人员姓名 as personName, 职务 as militaryPost, 级职 as militaryRank, 时间 as date , 前任 as predecessor
from 人事信息
where 单位ID = #{unitId}

select 单位ID as unitId, 单位名称 as unitName, 人员ID as personId, 人员姓名 as personName, 职务 as militaryPost, 级职 as militaryRank, 时间 as date , 前任 as predecessor
from 人事信息
where 单位名称 = #{unitName}

select 单位ID as unitId, 单位名称 as unitName, 人员ID as personId, 人员姓名 as personName, 职务 as militaryPost, 级职 as militaryRank, 时间 as date , 前任 as predecessor
from 人事信息
where 人员姓名 = #{personName}
